INSERT INTO `wp_bookly_news` (`id`, `news_id`, `title`, `media_type`, `media_url`, `text`, `button_url`, `button_text`, `seen`, `updated_at`, `created_at`) VALUES
(1, 79, 'New video: How to publish staff availability form', 'youtube', 'https://www.youtube.com/embed/TzZ3fa2MVtg', '<p>Learn how to add the Bookly front-end calendar to your webpage, which enables clients to view staff availability. This type of calendar only displays occupied time slots with the corresponding service title and does not allow bookings.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212973145-Keep-Track-and-Edit-Appointments\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212973145-Keep-Track-and-Edit-Appointments\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://youtu.be/TzZ3fa2MVtg', 'Watch on Youtube', 0, '2023-05-08 13:41:35', '2023-05-08 13:41:35'),
(2, 78, 'New version release. See what’s new in Bookly PRO 6.1.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2023/05/Bookly-PRO-6.1-banner-1512.jpg', '<p>As with every update, the latest version of Bookly offers enhanced features, optimizations, and bug fixes to ensure optimal performance. We\'ve included new options such as the ability to <strong>sell gift cards</strong> directly through the booking form, an <strong>SMTP mail gateway</strong> for improved email functionality, <strong>waiting list limits</strong> for services, and more.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v6-1/', 'Read more', 0, '2023-05-03 09:49:19', '2023-05-03 09:49:19'),
(3, 2, ' Mistakes every Bookly beginner should avoid', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/09/mistakes-every-bookly-beginner-should-avoid-banner-1512.jpg', '<p><span>Bookly admin panel is very intuitive and user-friendly. Thanks to its clean and simple design, even a WP beginner can</span><span>&nbsp;</span>set up Bookly<span>&nbsp;</span><span>without problems. We know you probably want to get started with online bookings on your website as quickly as possible and with almost no effort, so we’ve gathered the most common mistakes to avoid when you start using Bookly.</span></p>', 'https://www.booking-wp-plugin.com/mistakes-every-bookly-beginner-avoid/', 'Read more', 0, '2023-04-24 06:52:57', '2021-09-20 19:05:05'),
(4, 1, ' How to promote best selling services with Bookly', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/09/how-promote-best-selling-services-bookly-banner-1512.jpg', '<p><span>While Bookly is an appointment scheduling plugin that allows you to automate your online reservation system, some of its features can be used as a great way to gain customers’ interest in the services you want to promote. From this article, you’ll find out which Bookly items and built-in features you can use to better sell your services.</span></p>', 'https://www.booking-wp-plugin.com/promote-best-selling-services-bookly/', 'Read more', 0, '2023-04-24 06:46:38', '2021-09-20 19:02:23'),
(5, 44, 'How to get started with Bookly SMS mailing campaigns', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/02/how-to-get-started-with-bookly-sms-campaigns-banner-1512.jpg', '<p><span>Undoubtedly, the easiest and quickest way to reach your clients is by SMS. Bookly’s</span><span>&nbsp;</span><span>SMS service</span><span>&nbsp;</span><span>is already used by thousands of businesses for sending booking details confirmations, appointment reminders, and follow-ups. Now you can also take advantage of the</span><span>&nbsp;</span><span>automated SMS marketing campaigns</span><span>&nbsp;</span><span>that are configured and scheduled right in the Bookly admin area.</span></p>', 'https://www.booking-wp-plugin.com/get-started-bookly-sms-mailing-campaigns/', 'Read more', 0, '2023-04-04 16:34:12', '2022-02-01 08:58:09'),
(6, 40, 'How to add online meeting URL to any Bookly appointment', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/01/add-online-meeting-url-to-your-Bookly-appointment-1512.jpg', '<p><span>Nowadays, more and more companies embrace a remote approach in their operations. With Bookly, you get built-in integrations with the most popular software for online meetings: Zoom, Google Meet, and Jitsi. Whichever app you choose, it will automatically generate unique video meeting links for every appointment created via Bookly.</span></p>', 'https://www.booking-wp-plugin.com/add-online-meeting-url-bookly-appointment/', 'Read more', 0, '2023-04-04 16:32:05', '2022-01-18 08:35:40'),
(7, 46, 'How to limit daily bookings', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/02/how-to-limit-daily-bookings-in-bookly-banner-1512.jpg', '<p><span>Regardless of the staff members’ working schedule configured in Bookly, you may want to limit the number of appointments that your customers can schedule via your online booking page. Here we’ll describe which Bookly features you can use to allow only a certain amount of bookings during the staff working day.</span></p>', 'https://www.booking-wp-plugin.com/limit-daily-bookings/', 'Read more', 0, '2023-04-04 16:25:19', '2022-02-08 09:27:33'),
(8, 77, 'How to create time-flexible services in Bookly', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2023/03/how-to-create-time-flexible-services-in-bookly-banner-1512.jpg', '<p><span>Time-flexible services in Bookly mean appointments that may have changeable lengths, and clients can select a duration that fits them. Bookly offers several add-ons designed to meet specific scenarios where varying duration of the meeting is possible. While all of them may suit similar needs, each add-on differs in setup, customization, and management.</span></p>', 'https://www.booking-wp-plugin.com/create-time-flexible-services-bookly/', 'Read more', 0, '2023-04-04 06:15:16', '2023-04-04 06:15:16'),
(9, 76, 'New version release. See what’s new in Bookly PRO 6.0.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2023/03/Bookly-PRO-6.0-banner-1512.jpg', '<p>We regularly make updates to implement highly requested features and enhance the functionality of our products. This update includes several popular feature requests that will upgrade the Bookly booking system on your website. Discover new features and improvements included in<span>&nbsp;</span><strong>Bookly PRO version 6.0</strong>.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v6-0/', 'Read more', 0, '2023-03-16 11:09:42', '2023-03-16 11:09:42'),
(10, 75, 'New version release. See what’s new in Bookly PRO 5.9.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2023/02/Bookly-PRO-5.9-banner-1512.jpg', '<p><span>For a better experience and increased flexibility, we continue to develop and enhance Bookly. With this update, we introduce&nbsp;integration with<strong> WhatsApp</strong>, a new&nbsp;<strong>Bookly Staff</strong>&nbsp;booking&nbsp;<strong>form</strong>,&nbsp;customizable content of the service card&nbsp;in the modern booking forms,&nbsp;available payment methods for services, the ability to set&nbsp;default values in the new forms, and more.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v5-9/', 'Read more', 0, '2023-02-20 11:12:32', '2023-02-20 11:12:16'),
(11, 74, 'New video: How to generate invoices in Bookly', 'youtube', 'https://www.youtube.com/embed/gX_84Cvp3AM', '<p><span>With the Bookly Invoices (add-on), you can create detailed invoices for provided services and have them automatically sent when an appointment is booked, approved, or received. Learn how to set up and customize your templates.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360002713233\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360002713233\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=gX_84Cvp3AM&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-12-28 12:03:22', '2022-12-28 12:03:22'),
(12, 73, 'How to avoid gaps between appointments', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/12/avoid-gaps-between-appointments-banner-1512.jpg', '<p><span>Efficient schedule is very mportant for every business. Unwanted gaps between appointments are not convenient and may affect the whole balance of work.&nbsp;In this article, we’ll provide you with the tips that will help you optimize your Bookly calendar schedule and reduce empty slots as much as possible.</span></p>', 'https://www.booking-wp-plugin.com/avoid-gaps-between-appointments/', 'Read more', 0, '2022-12-06 14:40:35', '2022-12-06 14:40:35'),
(13, 72, 'New version release. See what’s new in Bookly PRO 5.6.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/11/Bookly-PRO-5.6-banner-1512.jpg', '<p><span>Our top priority is improving Bookly and developing new features that will work best for your business.&nbsp;In the current release, we introduce two new products –&nbsp;<strong>Square Payments</strong>&nbsp;and&nbsp;<strong>Voice Notifications</strong>, WooCommerce integration for the Bookly modern booking forms, Bookly Custom Fields (Add-on) &amp; Bookly Files (Add-on) compatibility with the new forms, and more.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v5-6/', 'Read more', 0, '2022-12-01 13:26:21', '2022-12-01 13:26:15'),
(14, 71, 'How to set up new Bookly Services booking form', 'youtube', 'https://www.youtube.com/embed/Qvp35ANbkUQ', '<p><span>One of the main benefits of the Bookly Services form is that you can organise services using categories and display them to your clients in a catalog view. Learn how to create your custom design and modify existing elements.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212178149-Booking-Form-Look-Feel\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212178149-Booking-Form-Look-Feel\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a> to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=Qvp35ANbkUQ&list=PLuLhoBV-rlAagLXlILG_1KgjVSaMUju8i', 'Watch on Youtube', 0, '2022-10-20 06:28:15', '2022-10-20 06:28:15'),
(15, 70, 'New version release. See what’s new in Bookly PRO 5.4.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/10/Bookly-PRO-5.4-banner-756.jpg', '<p>We track requests for new capabilities and keep working on the new functionality so that you can get more with Bookly every day! With this update, we release new features that meet the most popular requirements and help you manage your booking system easier and more effectively.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v5-4/', 'Read more', 0, '2022-10-10 11:31:59', '2022-10-10 11:31:59'),
(16, 69, 'New video: How to upload files in Bookly', 'youtube', 'https://www.youtube.com/embed/-ONIAAzIbmM', '<p><span>This video tutorial is about the file uploading feature in Bookly.&nbsp;</span><span>With the Bookly Files (Add-on), you can let your clients attach files related to the appointment right in the booking form.&nbsp;Uploaded documents will be stored at the appointment details.&nbsp;For more details, visit our&nbsp;<a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000373133\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000373133\"><span class=\"s1\">help center</span></a>&nbsp;and check other&nbsp;<a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\"><span class=\"s1\">video tutorials</span></a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=-ONIAAzIbmM&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-09-06 14:20:10', '2022-09-06 14:20:10'),
(17, 68, 'New version release. See what’s new in Bookly PRO 5.3.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/08/Bookly-PRO-5.3-banner-756.jpg', '<p><span>With this update, we release major&nbsp;</span>changes in the Appearance section<span>, the ability to&nbsp;</span>create several designs of your booking forms<span>,&nbsp;</span>payments in the new Bookly Search form<span>, and more. Also, as usual, new version includes&nbsp;general enhancements and bug fixes.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v5-3/', 'Read more', 0, '2022-08-25 13:00:11', '2022-08-25 13:00:11'),
(18, 67, 'New video: How to book identical appointments in a row', 'youtube', 'https://www.youtube.com/embed/xPsOXHMrly8', '<p>With the Bookly Multiply Appointments (Add-on), your clients can book back-to-back sessions of the same service.&nbsp;This way, customers can change the default appointment duration by selecting the quantity of similar services they’d like to get in a row.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210138469-Multiply-Appointments-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210138469-Multiply-Appointments-Add-on\" target=\"_blank\" rel=\"noopener\">help center</a> and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=xPsOXHMrly8&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-08-05 08:52:41', '2022-08-05 08:52:41'),
(19, 66, 'New version release. See what’s new in Bookly PRO 5.0.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/07/Bookly-PRO-5.0-banner-756.jpg', '<p>We continue to develop new features and make improvements in Bookly for a better experience and more flexibility. Today’s update brings a new appearance of the booking form, the ability to book packages in the Bookly search form, new Diagnostics section in the Bookly menu, and more.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v5-0/', 'Read more', 0, '2022-07-27 12:05:35', '2022-07-27 12:05:35'),
(20, 65, ' What’s the difference between Compound Services (Add-on) & Collaborative Services (Add-on)', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/07/difference-between-compound-addon-and-collaborative-addon-banner-756.jpg', '<p><span>Add-ons for Bookly have been created to add more advanced functionality to the default configuration of your Bookly PRO. If you have doubts about which extension is best for you, here’s a detailed comparison of the Compound Services (Add-on) and Collaborative Services (Add-on) for Bookly.</span></p>', 'https://www.booking-wp-plugin.com/whats-difference-compound-services-add-collaborative-services-add/', 'Read more', 0, '2022-07-12 10:24:47', '2022-07-12 10:24:47'),
(21, 64, 'New video: How to let clients rate your services', 'youtube', 'https://www.youtube.com/embed/k-iNqs4nBLY', '<p><span>With the Bookly Ratings (Add-on), you can gather customer feedback after each appointment and display staff members’ rating in a booking form. Learn how to quickly set up and use this feature.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360001695713\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360001695713\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=k-iNqs4nBLY&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-06-28 12:56:54', '2022-06-28 12:56:54'),
(22, 63, 'New video: How to create tasks in Bookly', 'youtube', 'https://www.youtube.com/embed/fT-J4Limf2Q', '<p>This video is about creating unscheduled appointments in Bookly. With the Bookly Tasks (Add-on), you can create and manage an unlimited number of tasks or bookings that have no date and time yet. For more details, visit our&nbsp;<a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360013214654-Skip-time-selection-Tasks-Add-on-\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360013214654-Skip-time-selection-Tasks-Add-on-\"><span class=\"s1\">help center</span></a>&nbsp;and check other&nbsp;<a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\"><span class=\"s1\">video tutorials</span></a>&nbsp;to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=fT-J4Limf2Q&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-06-17 14:55:12', '2022-06-17 14:55:12'),
(23, 62, 'New version release. See what’s new in Bookly PRO 4.9.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/06/Bookly-PRO-4.9-banner-756.jpg', '<p>This update brings a new product in Bookly Cloud – <strong>Cloud Cron</strong>, integration with BigBlueButton – virtual classroom software, customization of ICS files, read-only access for calendar in Staff Cabinet, new trigger in Zapier, improvements in the Files (Add-on), and more.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-9/', 'Read more', 0, '2022-06-13 11:14:55', '2022-06-13 11:14:55'),
(24, 61, 'New video: How to add online meeting URL to Bookly appointment', 'youtube', 'https://www.youtube.com/embed/KXpgB25Vs60', '<p>With Bookly, you get built-in integrations with the most popular software for online meetings: Zoom, Google Meet, and Jitsi. Whichever app you choose, it will generate unique video meeting links for each appointment created via Bookly.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360013355499\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360013355499\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=KXpgB25Vs60&list=PLuLhoBV-rlAagLXlILG_1KgjVSaMUju8i', 'Watch on Youtube', 0, '2022-06-06 17:10:12', '2022-06-06 17:10:12'),
(25, 60, 'New video: How to set up and use Bookly Compound Services (Add-on)', 'youtube', 'https://www.youtube.com/embed/iIJcQ8fT5BI', '<p>Learn how to combine existing services and spare time to make one complex appointment. Your clients can book such services as regular appointments, while Bookly adds reservations to each staff member’s schedule separately. For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360001642033-Compound-Services-Add-on\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360001642033-Compound-Services-Add-on\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=iIJcQ8fT5BI&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-05-24 09:39:45', '2022-05-24 09:39:45'),
(26, 59, 'New video: How to make chain bookings', 'youtube', 'https://www.youtube.com/embed/yGyzwKR9ktg', '<p>With the Chain Appointments (Add-on) for Bookly, your clients will be able to&nbsp;book several services in one booking session. Such appointments will be scheduled sequentially.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210316865-Chain-Appointments-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210316865-Chain-Appointments-Add-on\" target=\"_blank\" rel=\"noopener\">help center</a> and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=yGyzwKR9ktg&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-05-16 12:12:22', '2022-05-16 12:12:22'),
(27, 58, 'New version release. See what’s new in Bookly PRO 4.8.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/05/Bookly-PRO-4.8-banner-756.jpg', '<p>Bookly is making ongoing updates to improve your user experience and implement the most popular feature requests. Today’s update introduces the beta version of a new booking form, creation of WooCommerce orders for backend bookings, customization of the QR codes description, and more.</p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-8/', 'Read more', 0, '2022-05-12 15:35:28', '2022-05-12 15:35:28'),
(28, 57, 'Upcoming System Maintenance', 'image', 'https://images.booking-wp-plugin.com/main/blog/Upcoming-system-maintenance.jpg', '<p class=\"p1\">We would like to inform you that on Sunday-Monday night of <b>2022 May 09 from 00:00 (midnight) to 04:00 CET</b>, the SMS service will be temporarily unavailable to users in some regions or countries (Australia, Europe, Africa, Asia, South America). This time will be used to implement important upgrades and performance optimization.</p><p class=\"p1\">If you have any scheduled text messages during this time, please take this into consideration. We appreciate your patience and understanding!</p>', 'https://support.booking-wp-plugin.com/hc/en-us/articles/212411289-Set-Up-Text-Messages-SMS-', 'Help Center', 0, '2022-05-05 07:47:35', '2022-05-05 07:47:35'),
(29, 56, 'New video: How to create customer groups', 'youtube', 'https://www.youtube.com/embed/pNOayasDJXI', '<p>Bookly Customer Groups (Add-on) enables you to create and manage groups of clients for different purposes. You can set certain limitations, assign special prices, offer promotions to particular customers, and more.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000201873-Customer-Groups-Add-on\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000201873-Customer-Groups-Add-on\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=pNOayasDJXI&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-05-05 07:40:52', '2022-05-05 07:40:52'),
(30, 55, 'All you need to know about automatic discounts in Bookly', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/04/756x384.jpg', '<p>Coupon codes, automatic or conditional discounts, and special rates can be used during certain seasons, promotions, or at any time you wish in order to increase customers’ loyalty. Let’s see how a wide range of Bookly features can help you meet various requirements and encourage your clients to book more through your appointment booking form.</p>', 'https://www.booking-wp-plugin.com/automatic-discounts-bookly/', 'Read more', 0, '2022-04-28 10:33:53', '2022-04-28 10:33:53'),
(31, 54, 'New video: How to sync Bookly PRO with Google Calendar', 'youtube', 'https://www.youtube.com/embed/3swwpWJ7_-s', '<p>With Google Calendar and Bookly Pro, you can manage your staff workload and bookings with maximum efficiency. Learn how to set up the connection and enable synchronization of all appointments created in Bookly with the employee\'s personal calendar.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/203572552-Google-Calendar-Sync\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/203572552-Google-Calendar-Sync\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=3swwpWJ7_-s&list=PLuLhoBV-rlAagLXlILG_1KgjVSaMUju8i', 'Watch on Youtube', 0, '2022-04-25 16:05:42', '2022-04-25 16:05:42'),
(32, 53, 'New video: How to set flexible prices', 'youtube', 'https://www.youtube.com/embed/WfSbsoi06V8', '<p>Learn how to tweak the price for your services&nbsp;<span>on selected days of the week during&nbsp;cold or hot hours of the day. Dynamic pricing is available with the <strong>Special Hours (Add-on)</strong> for Bookly.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/213524185-Dynamic-Pricing-Special-Hours-Add-On-\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/213524185-Dynamic-Pricing-Special-Hours-Add-On-\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</span></p>', 'https://youtu.be/WfSbsoi06V8&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-04-19 08:42:45', '2022-04-19 08:42:45'),
(33, 52, 'New version release. See what’s new in Bookly PRO 4.7.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/04/Bookly-PRO-4.7-banner-756.jpg', '<p><span>Bookly rolls out a new option available for extending your active support period, the ability to create custom fields for backend entries, automatic update of the appointment status once it’s over, visible service price for appointments created via the Bookly admin panel, and more.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-7/', 'Read more', 0, '2022-04-07 11:27:12', '2022-04-07 11:27:12'),
(34, 51, 'New video: How to add service locations in Bookly', 'youtube', 'https://www.youtube.com/embed/KRlVIPE9xOA', '<p>With the Bookly Locations (Add-on),&nbsp;you can associate staff members with different locations, so your clients will be able to choose the place for their appointment before they schedule date and time.&nbsp;For more details, visit our <a href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210229885\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/210229885\">help center</a> and check other <a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=KRlVIPE9xOA&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-03-31 13:26:43', '2022-03-31 13:26:43'),
(35, 50, 'New video: How to create and manage promo codes in Bookly', 'youtube', 'https://www.youtube.com/embed/luOElg-mnNU', '<p>Bookly Coupons (Add-on) allows you to offer discounts for your services and attract more clients. This video describes how to quickly set up coupons in Bookly.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003939373\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003939373\" target=\"_blank\" rel=\"noopener\">help center</a> and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a> to learn the basics and get the most out of Bookly.</p>', 'https://www.youtube.com/watch?v=luOElg-mnNU&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-03-24 12:27:27', '2022-03-24 12:27:27'),
(36, 49, 'New video: How to create time-flexible services', 'youtube', 'https://www.youtube.com/embed/jYKG0eGvWJ0', '<p><span>Custom Duration (Add-on) for Bookly allows you to create services with flexible duration. On the booking page, your clients can choose the length of the appointment from the predefined options, and the price will be adjusted respectively.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360005082294-Services-with-flexible-duration-Custom-Duration-Add-on-\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360005082294-Services-with-flexible-duration-Custom-Duration-Add-on-\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=jYKG0eGvWJ0&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK?utm_source=bookly_admin&utm_medium=news', 'Watch on Youtube', 0, '2022-02-17 09:41:40', '2022-02-17 09:41:40'),
(37, 48, 'New version release. See what’s new in Bookly PRO 4.6.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/02/Bookly-PRO-4.6-banner-756.jpg', '<p><span>Bookly’s new update introduces QR codes, refunds for payments made via Stripe, separate payments for recurring appointments, alert counter for new appointments, default values in custom fields, ability to attach payment to the package, and more.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-6/?utm_source=bookly_admin&utm_medium=news', 'Read more', 0, '2022-02-15 16:13:28', '2022-02-15 16:13:28'),
(38, 47, 'New video: How to accept deposit payments', 'youtube', 'https://www.youtube.com/embed/ewK3byklkK0', '<p><span>Bookly Deposit Payments (Add-on)&nbsp;allows you to set up a minimum amount that customers should pay at the time of booking. A deposit&nbsp;is set individually for each staff member and each service.&nbsp;For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212234945-Deposit-Payments-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212234945-Deposit-Payments-Add-on\">help center</a>&nbsp;<span>and check other </span><a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=ewK3byklkK0&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-02-10 10:00:07', '2022-02-10 10:00:07'),
(39, 45, 'New video: How to add special days to staff schedule', 'youtube', 'https://www.youtube.com/embed/gi5eULm5-64', '<p><span>This video tutorial is about working schedule of your employees and changing regular timetable&nbsp;on particular days during the year.&nbsp;For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212817929-Extra-Long-Short-Days-Special-Days-Add-on-\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212817929-Extra-Long-Short-Days-Special-Days-Add-on-\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;<span>and check other </span><a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://youtu.be/gi5eULm5-64&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-02-03 08:46:00', '2022-02-03 08:46:00'),
(40, 43, 'New video: How to create recurring appointments', 'youtube', 'https://www.youtube.com/embed/tfUmuNrYrn8', '<p><span>Learn how to set up and create recurring appointments in Bookly. This feature allows your clients to make multiple bookings that repeat at regular intervals, so they can schedule the whole series at once.&nbsp;For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115001442665-Recurring-Appointments-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115001442665-Recurring-Appointments-Add-on\">help center</a>&nbsp;<span>and check other </span><a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=tfUmuNrYrn8&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-01-27 14:08:11', '2022-01-27 14:08:11'),
(41, 42, 'How to accept payments with WooCommerce', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/01/how-to-accept-payments-with-woocommerce-banner-756.jpg', '<p><span>If some payment gateway is not available in Bookly yet, you can have access to other payment options through WooCommerce – the world’s most popular e-commerce plugin for WordPress.&nbsp;Bookly PRO seamlessly integrates with WooCommerce allowing you to sell services as products.&nbsp;This article describes how to quickly set up the connection between two plugins.</span></p>', 'https://www.booking-wp-plugin.com/accept-payments-woocommerce/', 'Read more', 0, '2022-01-25 10:36:59', '2022-01-25 10:36:59'),
(42, 41, 'New video: How to book several appointments at once', 'youtube', 'https://www.youtube.com/embed/miYLCPCEYIg', '<p><span>Encourage your customers to book more on your website! This video explains how to set up the Bookly Cart (Add-on) and let your clients order a number of bookings in one session.&nbsp;For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212969945-Cart-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/212969945-Cart-Add-on\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;<span>and check other </span><a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=miYLCPCEYIg&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-01-20 09:42:21', '2022-01-20 09:42:21'),
(43, 39, 'New video: How to add extras to your services', 'youtube', 'https://www.youtube.com/embed/z_B7hyjBxqI', '<p><span>Extras are additional items that your customers can book along with the services. These may be </span><span>special tools, products, or resources for performing a service, or extra time if you set their duration.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/207647799-Extra-Products-Treatments-Service-Extras-Add-on-\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/207647799-Extra-Products-Treatments-Service-Extras-Add-on-\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=z_B7hyjBxqI&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-01-13 10:13:57', '2022-01-13 10:13:57'),
(44, 38, 'How to set specific booking days and times for a service', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/01/set-specific-booking-days-and-times-for-service-banner-756.jpg', '<p><span>Some services may be available on a seasonal basis, during specific hours within a day, or on certain days only.&nbsp;</span><span>Bookly allows you to adjust the service availability without affecting the regular timetable of your staff.&nbsp;</span><span>From this article, you will learn how to set up a custom schedule for a service to make it bookable only on special days and times.</span></p>', 'https://www.booking-wp-plugin.com/set-specific-booking-days-times-service/', 'Read more', 0, '2022-01-11 08:22:14', '2022-01-11 08:22:07'),
(45, 37, 'New video: How to set up Staff Cabinet (Add-on) for Bookly', 'youtube', 'https://www.youtube.com/embed/awHddbbNOaw', '<p><span>Personal Staff Cabinet for your employees enables them to view and manage the list of bookings, personal details, and schedules on the front end of your website. This video describes how to install and use it.&nbsp;For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003349989\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003349989\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;<span>and check other </span><a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=awHddbbNOaw&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2022-01-06 12:10:24', '2022-01-06 12:08:42'),
(46, 36, 'How to create a separate booking page for each staff member', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2022/01/how-to-create-separate-booking-page-for-staff-member-banner-756.jpg', '<p><span>If you want to invite your customers to an appointment with a certain staff member, you may want to create a custom booking page that includes staff details, featured services, information about special offers, etc. This article explains how to set up a personalized online booking form for your employee and provide your customers with a unique booking link.</span></p>', 'https://www.booking-wp-plugin.com/create-separate-booking-page/', 'Read more', 0, '2022-01-04 09:32:50', '2022-01-04 09:32:50'),
(47, 35, 'New video: How to limit the service availability', 'youtube', 'https://www.youtube.com/embed/8EEf0HbZwi0', '<p><span>Learn how to limit the hours during the day when the service can be booked, and which options you have when both Service Schedule (Add-on) and Special Days (Add-on) are installed and activated.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/213850385-Services-Available-at-Limited-Hours-Service-Schedule-Add-on-\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/213850385-Services-Available-at-Limited-Hours-Service-Schedule-Add-on-\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=8EEf0HbZwi0&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2021-12-30 15:25:12', '2021-12-30 10:32:17'),
(48, 34, 'New version release. See what’s new in Bookly PRO 4.5.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/12/Bookly-PRO-4.5-banner-756.jpg', '<p><span>The end of the year brings new features that we’re very excited to share with you! We want you to have a great experience using Bookly items, so the new release, as usual, includes some of the most requested improvements, general enhancements, and bug fixes.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-5/', 'Read more', 0, '2021-12-27 08:50:24', '2021-12-27 08:50:24'),
(49, 33, 'New video: How to accept group bookings', 'youtube', 'https://www.youtube.com/embed/Cmb1sxPFIMY', '<p><span>With the Bookly Group Booking (Add-on) you can use a capacity option, so your customers will be able to specify the number of persons they’d like to book for. Learn how to determine&nbsp;individual settings for each&nbsp;service and employee separately.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000259594-Group-Booking-Add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/360000259594-Group-Booking-Add-on\">help center</a>&nbsp;and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=Cmb1sxPFIMY&list=PLuLhoBV-rlAZpUnQgHJkMBx1rCvQGQ6cK', 'Watch on Youtube', 0, '2021-12-23 10:28:27', '2021-12-23 10:28:27'),
(50, 32, 'How to book appointments longer than 1 day', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/12/how-to-book-appointments-longer-than-one-day-banner-756.jpg', '<p><span>Some companies provide services that last throughout the day or even take several days, such as all-day conferences, weekend educational courses, or multi-day guided tours. Depending on the specific type of business, there are several options for setting up and managing such appointments via Bookly.</span></p>', 'https://www.booking-wp-plugin.com/book-appointments-longer-1-day/', 'Read more', 0, '2021-12-21 08:41:51', '2021-12-21 08:41:51'),
(51, 31, 'New video: How to extend client\'s profile in Bookly', 'youtube', 'https://www.youtube.com/embed/LvV2JDZV0uc', '<p><span>This video explains how to add custom data to the client’s profile with the Bookly Customer Information (Add-on). All customer-related information will always be available for you with a couple of clicks in the Customers section.&nbsp;For more details, visit our&nbsp;<a href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202869125-Manage-Clients\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202869125-Manage-Clients\">help center</a>&nbsp;and&nbsp;check other&nbsp;<a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://youtu.be/LvV2JDZV0uc&list=PLuLhoBV-rlAagLXlILG_1KgjVSaMUju8i', 'Watch on Youtube', 0, '2021-12-16 11:23:12', '2021-12-16 11:23:12'),
(52, 30, 'Top 5 automations to use in Bookly', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/12/Top-five-automations-to-use-in-Bookly-banner-756.jpg', '<p><span>Bookly is known to be the most feature-rich online booking and scheduling plugin in the market.&nbsp;In this post, we’ll provide an overview of the top 5 Bookly automations that will help you to manage your administrative workload with ease and improve your efficiency.</span></p>', 'https://www.booking-wp-plugin.com/top-5-automations-use-bookly/', 'Read more', 0, '2021-12-14 08:03:44', '2021-12-14 08:03:44'),
(53, 29, 'Discontinued support for Internet Explorer 11', 'image', 'https://images.booking-wp-plugin.com/main/blog/Internet_Explorer_discontinued_support.jpg', '<p><span>The Internet Explorer (IE) 11 desktop application </span><strong>will go out of support&nbsp;</strong><span>starting June 15, 2022. Bootstrap framework used for Bookly interface elements in the admin panel, </span><a href=\"https://blog.getbootstrap.com/2021/05/05/bootstrap-5/#browser-support\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://blog.getbootstrap.com/2021/05/05/bootstrap-5/#browser-support\">stopped supporting IE since version 5.0</a>&nbsp;<span>and </span><a href=\"https://wordpress.org/news/2021/05/dropping-support-for-internet-explorer-11/\" data-mce-href=\"https://wordpress.org/news/2021/05/dropping-support-for-internet-explorer-11/\">WordPress no longer supports IE 11 starting from version 5.8</a><span>. If you are currently using IE 11, it is strongly recommended that you switch to a more modern browser.</span></p>', 'https://blogs.windows.com/windowsexperience/2021/05/19/the-future-of-internet-explorer-on-windows-10-is-in-microsoft-edge/', 'Read more', 0, '2021-12-13 07:36:02', '2021-12-13 07:36:02'),
(54, 28, 'New video: How to manage customer base in Bookly', 'youtube', 'https://www.youtube.com/embed/K53xCEIo8NQ', '<p><span>With Bookly you get an unlimited, sortable and searchable customer list. But that’s not all. Learn how&nbsp;</span><span>Bookly allows you to effortlessly manage your customer base. For more details, visit our </span><a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202869125-Manage-Clients\" href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202869125-Manage-Clients\" target=\"_blank\" rel=\"noopener\">help center</a><span>&nbsp;and&nbsp;check other </span><a href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\" data-mce-href=\"https://www.youtube.com/c/Bookly/videos\">video tutorials</a>&nbsp;<span>to learn the basics and get the most out of Bookly.</span></p>', 'https://www.youtube.com/watch?v=K53xCEIo8NQ&list=PLuLhoBV-rlAbu0MMy7yG8KeysVAEdBOcc', 'Watch on Youtube', 0, '2021-12-09 09:28:31', '2021-12-09 09:28:31'),
(55, 27, 'What’s the difference between Multiply Appointments (Add-on) & Custom Duration (Add-on)', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/12/difference-between-multiply-appointments-addon-and-custom-duration-addon-banner-756.jpg', '<p><span>Add-ons for Bookly have been created to add more advanced functionality to the default configuration of your Bookly PRO. If you have doubts about which extension is best for you, here’s a detailed comparison of the Multiply Appointments (Add-on) and Custom Duration (Add-on) for Bookly.</span></p>', 'https://www.booking-wp-plugin.com/whats-difference-multiply-appointments-add-custom-duration-add/', 'Read more', 0, '2021-12-07 09:19:54', '2021-12-07 09:19:54'),
(56, 26, 'New video: How to receive and manage online payments', 'youtube', 'https://www.youtube.com/embed/5WaXF4AeG-A', '<p><span>There are&nbsp;more than </span><strong>10 payment gateways&nbsp;</strong><span>which can be integrated with Bookly PRO to help drive more customers from the booking plugin right to your door. Learn how to quickly enable a desired online payment option in Bookly settings. For more details, visit our&nbsp;</span><a href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202811029-Pricing-Payment\" data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/sections/202811029-Pricing-Payment\" target=\"_blank\" rel=\"noopener\">help center</a><span>.</span></p>', 'https://www.youtube.com/watch?v=5WaXF4AeG-A&list=PLuLhoBV-rlAagLXlILG_1KgjVSaMUju8i', 'Watch on Youtube', 0, '2021-12-02 09:08:52', '2021-12-02 09:08:52'),
(57, 25, ' How to never miss an appointment', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/11/how-to-never-miss-an-appointment-with-Bookly-756.jpg', '<p><span>The last thing any business owner or service provider would want is to miss an appointment with a client. No matter how long is your booking list, Bookly will help you to efficiently control your company workload. With these recommendations, you will have no chance to forget or miss a scheduled meeting.</span></p>', 'https://www.booking-wp-plugin.com/never-miss-appointment-bookly/', 'Read more', 0, '2021-11-30 08:06:35', '2021-11-30 08:06:35'),
(58, 24, 'New video: How to set up and use custom fields in Bookly', 'youtube', 'https://www.youtube.com/embed/yRqrXj83eoY', '<p><span>This video explains how</span><span>&nbsp;to add your own custom questions to the booking form with the <strong>Bookly Custom Fields (Add-on)</strong>.&nbsp;For more details, visit our <a data-mce-href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003963514-Custom-Fields-add-on\" href=\"https://support.booking-wp-plugin.com/hc/en-us/articles/115003963514-Custom-Fields-add-on\" target=\"_blank\" rel=\"noopener\">help center</a>&nbsp;and check other <a data-mce-href=\"https://www.youtube.com/c/Bookly/videos\" href=\"https://www.youtube.com/c/Bookly/videos\" target=\"_blank\" rel=\"noopener\">video tutorials</a>&nbsp;to learn the basics and get the most out of Bookly.</span></p>', 'https://youtu.be/yRqrXj83eoY', 'Watch on Youtube', 0, '2021-11-26 08:21:57', '2021-11-26 08:21:57');
INSERT INTO `wp_bookly_news` (`id`, `news_id`, `title`, `media_type`, `media_url`, `text`, `button_url`, `button_text`, `seen`, `updated_at`, `created_at`) VALUES
(59, 23, 'Introducing Bookly Mailchimp (Add-on)', 'image', 'https://images.booking-wp-plugin.com/main/products/Bookly-Mailchimp-integration.jpg', '<p><span>We are happy to announce our new integration with&nbsp;</span><strong>Mailchimp&nbsp;</strong><span>– one of the most popular and flexible email marketing platforms that you can use for scheduling and </span><strong>automating your email campaigns</strong><span>. Once connected, you\'ll be able to&nbsp;automatically export client\'s contact information to your mailing list after an appointment is created via Bookly.</span></p>', 'https://codecanyon.net/item/bookly-mailchimp-addon/34925755?ref=ladela', 'Learn more', 0, '2021-11-24 10:29:57', '2021-11-24 10:29:57'),
(60, 22, 'New version release. See what’s new in Bookly PRO 4.4.', 'image', 'https://www.booking-wp-plugin.com/wp-content/uploads/2021/11/Bookly-PRO-release-4.4-756.jpg', '<p><span>Bookly is continually getting better to provide you with the most helpful features for your business. This update brings most-requested features, general improvements, and fixes to ensure that Bookly is working at its best.&nbsp;Check out the list of capabilities included in&nbsp;</span><strong>Bookly PRO version 4.4</strong><span>.</span></p>', 'https://www.booking-wp-plugin.com/bookly-pro-v4-4/', 'Read more', 0, '2021-11-22 12:25:15', '2021-11-22 12:25:15');
